import React from 'react';
import {
  Home,
  MessageSquare,
  Search,
  Shield,
  MessageCircle,
  LogOut,
  Building2,
} from 'lucide-react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAppContext } from '../context/AppContext';
import { Button } from './ui/button';
import { Badge } from './ui/badge';

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { state, logoutUser, toggleChatbot } = useAppContext();
  const { user } = state;

  const handleLogout = () => {
    logoutUser();
    navigate('/institution-select');
  };

  const navigationItems = [
    {
      label: 'Anonymous Feedback',
      path: '/anonymous-feedback',
      icon: <MessageSquare className="h-4 w-4" />,
      public: true,
    },
    {
      label: 'Track Status',
      path: '/status-tracking',
      icon: <Search className="h-4 w-4" />,
      public: true,
    },
  ];

  const authenticatedNavItems = [
    {
      label: 'Dashboard',
      path: '/dashboard',
      icon: <Home className="h-4 w-4" />,
    },
    ...(user.selectedRole?.name === 'Admin' || user.selectedRole?.name === 'Grievance Officer'
      ? [
          {
            label: 'Admin Dashboard',
            path: '/admin-dashboard',
            icon: <Shield className="h-4 w-4" />,
          },
        ]
      : []),
  ];

  const currentItems = user.isAuthenticated
    ? [...authenticatedNavItems, ...navigationItems]
    : navigationItems;

  return (
    <AppBar 
      position="static" 
      elevation={0}
      sx={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(255,255,255,0.1)',
      }}
    >
      <Toolbar sx={{ py: 1 }}>
        <Box 
          display="flex" 
          alignItems="center" 
          gap={2}
          sx={{ flexGrow: 1, cursor: 'pointer' }}
          onClick={() => navigate('/')}
        >
          <Avatar
            sx={{
              width: 40,
              height: 40,
              background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)',
              boxShadow: '0 4px 12px rgba(99, 102, 241, 0.3)',
            }}
          >
            <AccountBalanceIcon />
          </Avatar>
          <Typography
            variant="h6"
            component="div"
            sx={{
              fontWeight: 700,
              color: 'white',
              fontSize: '1.25rem',
            }}
          >
            Grievance Portal
          </Typography>
        </Box>

        {/* Institution and Role Display */}
        {user.selectedInstitution && (
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mr: 3 }}>
            <Chip
              label={user.selectedInstitution.name}
              size="small"
              sx={{
                backgroundColor: 'rgba(255,255,255,0.15)',
                color: 'white',
                border: '1px solid rgba(255,255,255,0.2)',
                fontWeight: 500,
                '& .MuiChip-label': { 
                  color: 'white',
                  px: 1.5,
                },
              }}
            />
            {user.selectedRole && (
              <Chip
                label={user.selectedRole.name}
                size="small"
                sx={{
                  backgroundColor: 'rgba(255,255,255,0.15)',
                  color: 'white',
                  border: '1px solid rgba(255,255,255,0.2)',
                  fontWeight: 500,
                  '& .MuiChip-label': { 
                    color: 'white',
                    px: 1.5,
                  },
                }}
              />
            )}
          </Box>
        )}

        {/* Navigation Items */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
          {currentItems.map((item) => (
            <Button
              key={item.path}
              color="inherit"
              startIcon={item.icon}
              onClick={() => navigate(item.path)}
              sx={{
                borderRadius: 2,
                px: 2,
                py: 1,
                fontWeight: 500,
                backgroundColor:
                  location.pathname === item.path
                    ? 'rgba(255,255,255,0.15)'
                    : 'transparent',
                border: location.pathname === item.path 
                  ? '1px solid rgba(255,255,255,0.2)' 
                  : '1px solid transparent',
                '&:hover': {
                  backgroundColor: 'rgba(255,255,255,0.1)',
                  border: '1px solid rgba(255,255,255,0.2)',
                },
                transition: 'all 0.2s ease-in-out',
              }}
            >
              {item.label}
            </Button>
          ))}

          {/* Chatbot Toggle */}
          <Tooltip title="Open Help Chat">
            <IconButton
              color="inherit"
              onClick={toggleChatbot}
              sx={{
                ml: 1,
                backgroundColor: state.chatbotOpen
                  ? 'rgba(255,255,255,0.15)'
                  : 'transparent',
                border: state.chatbotOpen 
                  ? '1px solid rgba(255,255,255,0.2)' 
                  : '1px solid transparent',
                '&:hover': {
                  backgroundColor: 'rgba(255,255,255,0.1)',
                  border: '1px solid rgba(255,255,255,0.2)',
                },
                transition: 'all 0.2s ease-in-out',
              }}
            >
              <ChatIcon />
            </IconButton>
          </Tooltip>

          {/* Logout Button */}
          {user.isAuthenticated && (
            <Tooltip title="Logout">
              <IconButton 
                color="inherit" 
                onClick={handleLogout}
                sx={{
                  ml: 0.5,
                  '&:hover': {
                    backgroundColor: 'rgba(255,255,255,0.1)',
                    border: '1px solid rgba(255,255,255,0.2)',
                  },
                  transition: 'all 0.2s ease-in-out',
                }}
              >
                <LogoutIcon />
              </IconButton>
            </Tooltip>
          )}
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
